UPDATE `command` SET `help`='Sintaxis: .guild invite [$NombrePersonaje] \"$NombreHermandad\"\r\nAgrega al personaje $NombrePersonaje (o el seleccionado) a la hermandad $NombreHermandad. El nombre de la hermandad debe ir entre comillas dobles.' WHERE `name`='guild invite';
