UPDATE `command` SET `help`='Sintaxis: .honor update\r\nFuerza que los campos de honor de ayer sean actualizados con los datos de hoy, el cual será reiniciado para el personaje seleccionado.' WHERE `name`='honor update';
UPDATE `command` SET `help`='Sintaxis: .hover #flag\r\nHabilita o deshabilita el modo suspendido a tu personaje.\r\nPon #flag 1 para habilitar o 0 para deshabilitar el modo suspendido.' WHERE `name`='hover';
UPDATE `command` SET `help`='Sintaxis: .instance $subcomando\nEscriba .instance para ver la lista de posibles subcomandos o .help instance $subcomando para ver la información sobre ese subcomando' WHERE `name`='instance';
