UPDATE `command` SET `help`='Sintaxis: .kick [$Jugador] [$razon]\r\nExpulsa al jugador nombrado del servidor con o sin ninguna razón. Si no se indica el nombre del personaje, el jugador seleccionado (a excepción de ti mismo) será expulsado. Si no se indica la razón, por defecto será \"Sin ninguna razón\".' WHERE `name`='kick';
UPDATE `command` SET `help`='Sintaxis: .learn #spell [all]\r\nEl jugador seleccionado aprende un hechizo de id #spell. Si se añade \'all\' aprenderá todas las ramas de hechizos.' WHERE `name`='learn';
