UPDATE `command` SET `help`='Sintaxis: .modify faction #Idfaccion #IdFlag #IdNpcFlag #IdDynamicFlag\r\nModifica la facción y las flags del NPC seleccionado. Sin argumentos, muestra la facción y las flags del NPC seleccionado.' WHERE `name`='modify faction';
UPDATE `command` SET `help`='Sintaxis: .modify fly #ratio\nModifica la velocidad de vuelo del jugador seleccionado tomando como referencia \"velocidad normal de vuelo\"*ratio. Si no hay un jugador seleccionado, modifica tu velocidad.\n El #ratio puede ser de 0.1 a 50.' WHERE `name`='modify fly';
