UPDATE `command` SET `help`='Sintaxis: .modify money #cantidad\r\n.money #cantidad\r\nAñade o remueve dinero al jugador seleccionado. Si no hay un jugador seleccionado, modifica tu dinero.\r\n #cantidad puede ser negativo para remover dinero.' WHERE `name`='modify money';
